document.addEventListener("DOMContentLoaded",()=>{const pages=document.querySelectorAll(".page");const navLinks=document.querySelectorAll(".main-nav a");const productList=document.querySelector(".product-list");const templateSource=document.querySelector("#template-product").innerHTML;const productTemplate=Handlebars.compile(templateSource);function activatePage(id){pages.forEach(page=>{page.classList.toggle("active",page.id===id)});navLinks.forEach(link=>{link.classList.toggle("active",link.getAttribute("href")==="#"+id)});if(id==="products"){productList.innerHTML="";fetch("http://localhost:3131/products").then(res=>res.json()).then(data=>{data.forEach(product=>{const html=productTemplate(product);const wrapper=document.createElement("div");wrapper.innerHTML=html;productList.appendChild(wrapper.firstElementChild)})}).catch(err=>{console.error("Błąd podczas pobierania produktów:",err)})}}const startPage=window.location.hash.replace("#","")||"home";activatePage(startPage);navLinks.forEach(link=>{link.addEventListener("click",e=>{e.preventDefault();const targetId=link.getAttribute("href").replace("#","");activatePage(targetId);window.location.hash="#"+targetId})})});